import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.css']
})
export class CalendarComponent implements OnInit {

  eventForm: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.eventForm = this.fb.group({
      title: ['', Validators.required],
      description: [''],
      start: ['', Validators.required],
      end: ['', Validators.required],
      location: ['']
    });
  }

  onSubmit(): void {
    // Handle creating the Google Calendar event here
    // You can access the form values using this.eventForm.value
  }

}
<form [formGroup]="eventForm" (ngSubmit)="onSubmit()">
  <label for="title">Title:</label>
  <input type="text" id="title" formControlName="title" required>

  <label for="description">Description:</label>
  <textarea id="description" formControlName="description"></textarea>

  <label for="start-time">Start Time:</label>
  <input type="datetime-local" id="start-time" formControlName="startTime" required>

  <label for="end-time">End Time:</label>
  <input type="datetime-local" id="end-time" formControlName="endTime" required>

  <button type="submit" [disabled]="eventForm.invalid">Create Event</button>
</form>
